import React, { useState } from 'react';
import './App.css';
import InputField from './components/InputField';
import { Todo } from './model';
import TodoList from './components/TodoList';
import {DragDropContext, DropResult} from 'react-beautiful-dnd'
// import { constants } from 'buffer';


const App: React.FC = () => {

  const [todo, setTodo] = useState<string>(""); 
  const [todos, setTodos] = useState<Todo[]>([]);
  const [completedTodos, setCompletedTodos] = useState<Todo[]>([]);

  const handleAdd = (e: React.FormEvent) => {
    e.preventDefault()

    if (todo) {
      setTodos([...todos, {id: Date.now(), todo, isDone: false}])
      setTodo("")
    }
  }

  const onDragEnd = (result: DropResult) => {
    const { destination, source } = result;

    console.log(result);

    if (!destination ) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index

    ) {
      console.log("destination index" + destination.index)

      return;
    }

    let add;
    let active = todos;
    let complete = completedTodos;


    if (completedTodos.length === 3) {
      const removed = completedTodos.pop(); // remove last element from completedTodos
      if (removed) { // check if removed is defined
        const newTodos = [removed, ...todos]; // add removed element to the start of the newTodos array
        setCompletedTodos([...completedTodos]); // update completedTodos state
        setTodos(newTodos); // update todos state
      }
    }


    // Source Logic
    if (source.droppableId === "TodoList") {
      add = active[source.index];
      active.splice(source.index, 1);
    } else {
      add = complete[source.index];
      complete.splice(source.index, 1);
    }

    // Destination Logic
    if (destination.droppableId === "TodoList") {
      active.splice(destination.index, 0, add);
    } else {
      complete.splice(destination.index, 0, add);
    }

    setCompletedTodos(complete);
    setTodos(active);


  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="App">
        <span className="heading">Taskify</span>
        <InputField todo={todo} setTodo={setTodo} handleAdd={handleAdd} />
        <TodoList
          todos={todos}
          setTodos={setTodos}
          completedTodos={completedTodos}
          setCompletedTodos={setCompletedTodos}
        />
      </div>
    </DragDropContext>
  );
}

export default App;
